import React, { useState, useEffect } from 'react';
import axios from 'axios';

function VehicleList({ vehicles }) {
  const [vehicleData, setVehicleData] = useState(vehicles);
  const [editingVehicle, setEditingVehicle] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    price: ''
  });

  useEffect(() => {
    setVehicleData(vehicles);
  }, [vehicles]);

  // Handle form data change
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Handle form submit for creating or editing a vehicle
  const handleFormSubmit = async (e) => {
    e.preventDefault();

    if (editingVehicle) {
      // Edit vehicle
      try {
        await axios.put(`http://localhost:5000/api/vehicles/${editingVehicle._id}`, formData);
        setVehicleData(vehicleData.map(vehicle => vehicle._id === editingVehicle._id ? { ...vehicle, ...formData } : vehicle));
        setEditingVehicle(null);
      } catch (error) {
        console.error('Error updating vehicle', error);
      }
    } else {
      // Add new vehicle
      try {
        const response = await axios.post('http://localhost:5000/api/vehicles', formData);
        setVehicleData([...vehicleData, response.data]);
      } catch (error) {
        console.error('Error adding vehicle', error);
      }
    }

    setFormData({ name: '', price: '' }); // Clear form after submit
  };

  // Handle Delete
  const handleDelete = async (vehicleId) => {
    try {
      await axios.delete(`http://localhost:5000/api/vehicles/${vehicleId}`);
      setVehicleData(vehicleData.filter(vehicle => vehicle._id !== vehicleId));
    } catch (error) {
      console.error('Error deleting vehicle', error);
    }
  };

  // Handle Edit
  const handleEdit = (vehicle) => {
    setEditingVehicle(vehicle);
    setFormData({
      name: vehicle.name,
      price: vehicle.price
    });
  };

  return (
    <div>
      <h2>Vehicles</h2>

      {/* Vehicle Form for Add/Edit */}
      <form onSubmit={handleFormSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Vehicle Name"
          value={formData.name}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Vehicle Price"
          value={formData.price}
          onChange={handleInputChange}
          required
        />
        <button type="submit">{editingVehicle ? 'Update Vehicle' : 'Add Vehicle'}</button>
      </form>

      {/* Table for Vehicles */}
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {vehicleData.length > 0 ? (
            vehicleData.map((vehicle) => (
              <tr key={vehicle._id}>
                <td>{vehicle.name}</td>
                <td>{vehicle.price}</td>
                <td>
                  <button onClick={() => handleEdit(vehicle)}>Edit</button>
                  <button onClick={() => handleDelete(vehicle._id)}>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3">No vehicles found</td>
            </tr>
          )}
        </tbody>
      </table>

      <button onClick={() => setEditingVehicle(null)}>Create New Vehicle</button>
    </div>
  );
}

export default VehicleList;
