import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Sidebar from './Sidebar';

function UsersList({ users = [] }) {
  const [vehicleData, setVehicleData] = useState(users);
  const [editingVehicle, setEditingVehicle] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
   
  });

  useEffect(() => {
    setVehicleData(users);
  }, [users]);

  // Handle form data change
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="main-content">
        <h2>users</h2>

        {/* Vehicle Form for Add/Edit */}
        <form onSubmit={handleFormSubmit} className="vehicle-form">
          <input
            type="text"
            name="make"
            placeholder="Make"
            value={formData.make}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="model"
            placeholder="Model"
            value={formData.model}
            onChange={handleInputChange}
            required
          />
          <input
            type="number"
            name="year"
            placeholder="Year"
            value={formData.year}
            onChange={handleInputChange}
            required
          />
          <input
            type="number"
            name="rental_price"
            placeholder="Rental Price"
            value={formData.rental_price}
            onChange={handleInputChange}
            required
          />
          <button type="submit">{editingVehicle ? 'Update Vehicle' : 'Add Vehicle'}</button>
        </form>

        {/* Table for users */}
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Make</th>
                <th>Model</th>
                <th>Year</th>
                <th>Rental Price</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {vehicleData.length > 0 ? (
                vehicleData.map((vehicle) => (
                  <tr key={vehicle._id}>
                    <td>{vehicle.make}</td>
                    <td>{vehicle.model}</td>
                    <td>{vehicle.year}</td>
                    <td>{vehicle.rental_price}</td>
                    <td>
                      <button onClick={() => handleEdit(vehicle)}>Edit</button>
                      <button onClick={() => handleDelete(vehicle._id)}>Delete</button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5">No users found</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default UsersList;
