import React, { useState, useEffect } from 'react';
import axios from 'axios';

function VehicleList({ vehicles }) {
  const [vehicleData, setVehicleData] = useState(vehicles);

  useEffect(() => {
    // If the vehicles prop changes, update the vehicleData state
    setVehicleData(vehicles);
  }, [vehicles]); // Run whenever vehicles prop changes

  const handleDelete = async (vehicleId) => {
    try {
      await axios.delete(`http://localhost:5000/api/vehicles/${vehicleId}`);
      setVehicleData(vehicleData.filter(vehicle => vehicle._id !== vehicleId));
    } catch (error) {
      console.error('Error deleting vehicle', error);
    }
  };

  return (
    <div>
      <h2>Vehicles</h2>
      <ul>
        {vehicleData.length > 0 ? (
          vehicleData.map((vehicle) => (
            <li key={vehicle._id}>
              {vehicle.name} - {vehicle.price}
              <button onClick={() => handleDelete(vehicle._id)}>Delete</button>
              <button>Edit</button>
            </li>
          ))
        ) : (
          <p>No vehicles found</p>
        )}
      </ul>
      <button>Create Vehicle</button>
    </div>
  );
}

export default VehicleList;
